var gulp=require("gulp"),connect=require("gulp-connect"),jade=require("gulp-jade"),opn=require("opn");gulp.task("connect",function(){connect.server({root:"app",livereload:!0,port:8888}),opn("http://localhost:8888")}),gulp.task("html",function(){gulp.src("app/*.html").pipe(connect.reload())}),gulp.task("css",function(){gulp.src("app/css/*.css").pipe(connect.reload())}),gulp.task("js",function(){gulp.src("app/js/*.js").pipe(connect.reload())}),gulp.task("jade",function(){gulp.src("app/jade/*.jade").pipe(jade({pretty:!0})).on("error",console.log).pipe(gulp.dest("app/")).pipe(connect.reload())}),gulp.task("watch",function(){gulp.watch(["app/*.html"],["html"]),gulp.watch(["app/css/*.css"],["css"]),gulp.watch(["app/js/*.js"],["js"]),gulp.watch(["app/jade/*.jade"],["jade"])}),gulp.task("default",["connect","watch"]);